descriptor.controllerScript:
var canvas;
var ctx1;
var heat;
var mapa = Array(40000).fill();
var temperaturas = {"t1": 0, "t2": 0, "t3": 0, "t4": 0 };

self.onInit = function() {

    self.ctx.hideTitlePanel = true;
    self.ctx.$scope.datasources = self.ctx.defaultSubscription.datasources; // Definir datasources igual al de la defaultSubscription
    self.ctx.$scope.data = self.ctx.defaultSubscription.data; // Definir data igual al de la defaultSubscription

    self.ctx.$scope.datasourceData = [];

    var currentDatasource = null;
    var currentDatasourceIndex = -1;

    for (var i=0;i<self.ctx.$scope.data.length;i++) {
        var dataKeyData = self.ctx.$scope.data[i];
        if (dataKeyData.datasource != currentDatasource) {
            currentDatasource = dataKeyData.datasource
            currentDatasourceIndex++;
            self.ctx.$scope.datasourceData[currentDatasourceIndex] = [];

        } 
        self.ctx.$scope.datasourceData[currentDatasourceIndex].push(dataKeyData);
    }
    self.ctx.updateWidgetParams();

    canvas = document.getElementById("canvas");
    ctx1 = canvas.getContext("2d");

    heat = simpleheat(canvas);

    // set point radius and blur radius
    heat.radius(3, 10);


    // set max data value
    heat.max(30);
}

self.onDataUpdated = function(){

    // Interlpolo solo si recibo por lo menos una temperatura por telemetria
    if (self.ctx.data.length > 0)  {
        interpolar();
        heat.data(mapa);
        heat.draw();
    }
}

self.onResize = function() {
    heat.radius(canvas.width / 300, 10);
    canvas.height = self.ctx.height;
    canvas.width = self.ctx.width;
    heat.resize();
}

function interpolar() {

    // Actualizo las temperaturas que hayan cambiado
    for (let i = 0; i < self.ctx.data.length; i++) {

        let data_i = self.ctx.data[i].data;

        // Si cambio la temperatura en ti actualizo ti
        if (data_i.length > 0) {

            // Agarro la temperatura mas reciente
            temperaturas["t" + (i+1)] =  data_i[data_i.length - 1][1];
        }  
    }

    console.log(temperaturas);
    let t1 = temperaturas["t1"], t2 = temperaturas["t2"], t3 = temperaturas["t3"], t4 = temperaturas["t4"];

    let map = Array(200).fill().map(()=>Array(200).fill());
    let cAncho = canvas.width/200;   //Coeficientes para la escala
    let cAlto = canvas.height/200;    // Alto(O ANCHO) / 100

    deltaT1 = (t3-t1)/80;
    deltaT2 = (t4-t2)/80;
    let cant = 0;

    for(let i = 0; i<200; i++){
        map[i][9] = deltaT1*(i-9) + t1;
        map[i][89] = deltaT2*(i-9) + t2;
    }

    for(let i = 0; i<200; i++){

        deltaT = (map[i][89]-map[i][9])/80;

        for(let j = 0; j<200; j++){

            map[i][j] = deltaT*(j-9) + map[i][9];

            mapa[cant] = [j*cAncho,i*cAlto,map[i][j]];
            cant++;
        }
    }

}

// Codigo de: https://github.com/mourner/simpleheat/tree/gh-pages
function simpleheat(canvas) {
    if (!(this instanceof simpleheat)) return new simpleheat(canvas);

    this._canvas = canvas = typeof canvas === 'string' ? document.getElementById(canvas) : canvas;

    this._ctx = canvas.getContext('2d');
    this._width = canvas.width;
    this._height = canvas.height;

    this._max = 1;
    this._data = [];
}

simpleheat.prototype = {

    defaultRadius: 25,

    defaultGradient: {
        0.4: 'blue',
        0.6: 'cyan',
        0.7: 'lime',
        0.8: 'yellow',
        1.0: 'red'
    },

    data: function (data) {
        this._data = data;
        return this;
    },

    max: function (max) {
        this._max = max;
        return this;
    },

    add: function (point) {
        this._data.push(point);
        return this;
    },

    clear: function () {
        this._data = [];
        return this;
    },

    radius: function (r, blur) {
        blur = blur === undefined ? 15 : blur;

        // create a grayscale blurred circle image that we'll use for drawing points
        var circle = this._circle = this._createCanvas(),
            ctx = circle.getContext('2d'),
            r2 = this._r = r + blur;

        circle.width = circle.height = r2 * 2;

        ctx.shadowOffsetX = ctx.shadowOffsetY = r2 * 2;
        ctx.shadowBlur = blur;
        ctx.shadowColor = 'black';

        ctx.beginPath();
        ctx.arc(-r2, -r2, r, 0, Math.PI * 2, true);
        ctx.closePath();
        ctx.fill();

        return this;
    },

    resize: function () {
        this._width = this._canvas.width;
        this._height = this._canvas.height;
    },

    gradient: function (grad) {
        // create a 256x1 gradient that we'll use to turn a grayscale heatmap into a colored one
        var canvas = this._createCanvas(),
            ctx = canvas.getContext('2d'),
            gradient = ctx.createLinearGradient(0, 0, 0, 256);

        canvas.width = 1;
        canvas.height = 256;

        for (var i in grad) {
            gradient.addColorStop(+i, grad[i]);
        }

        ctx.fillStyle = gradient;
        ctx.fillRect(0, 0, 1, 256);

        this._grad = ctx.getImageData(0, 0, 1, 256).data;

        return this;
    },

    draw: function (minOpacity) {
        if (!this._circle) this.radius(this.defaultRadius);
        if (!this._grad) this.gradient(this.defaultGradient);

        var ctx = this._ctx;

        ctx.clearRect(0, 0, this._width, this._height);

        // draw a grayscale heatmap by putting a blurred circle at each data point
        for (var i = 0, len = this._data.length, p; i < len; i++) {
            p = this._data[i];
            ctx.globalAlpha = Math.min(Math.max(p[2] / this._max, minOpacity === undefined ? 0.05 : minOpacity), 1);
            ctx.drawImage(this._circle, p[0] - this._r, p[1] - this._r);
        }

        // colorize the heatmap, using opacity value of each pixel to get the right color from our gradient
        var colored = ctx.getImageData(0, 0, this._width, this._height);
        this._colorize(colored.data, this._grad);
        ctx.putImageData(colored, 0, 0);

        return this;
    },

    _colorize: function (pixels, gradient) {
        for (var i = 0, len = pixels.length, j; i < len; i += 4) {
            j = pixels[i + 3] * 4; // get gradient color from opacity value

            if (j) {
                pixels[i] = gradient[j];
                pixels[i + 1] = gradient[j + 1];
                pixels[i + 2] = gradient[j + 2];
            }
        }
    },

    _createCanvas: function () {
        if (typeof document !== 'undefined') {
            return document.createElement('canvas');
        } else {
            // create a new canvas instance in node.js
            // the canvas class needs to have a default constructor without any parameter
            return new this._canvas.constructor();
        }
    }
};

